import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Location } from '@angular/common';

@Component({
  selector: 'app-customer-data',
  templateUrl: './customer-data.component.html',
  styleUrls: ['./customer-data.component.css']
})
export class CustomerDataComponent {
  customer: any = { name: '', address: '' ,phone:'', email:''};
  selectedItems: any[] = [];
  errorMessage: string = '';

  constructor(private router: Router,private location: Location) {
    const navigation = this.router.getCurrentNavigation();
    if (navigation?.extras.state) {
      this.selectedItems = navigation.extras.state['selectedItems'];
    }
  }
  GoBack(){
    this.location.back()
  }


  confirm() {
    if (this.customer.name ==''|| this.customer.address =='' ||this.customer.phone=='') {
      this.errorMessage = 'Please fill in all fields.';
      return;
    }

    else{
    this.errorMessage = '';
    const order = this.createOrder();
    this.router.navigate(['/yourdata'], { state: { order} });

    console.log(order)}
  }

  createOrder() {
    return {
      items: this.selectedItems,
      customerName: this.customer.name,
      customerAddress: this.customer.address,
      customerEmail:this.customer.email,
    customerPhone :this.customer.phone
    };
  }
  onSubmit(form: any) {
    if (form.valid) {
      console.log('Form Submitted!', form.value);
    }
  }
}

<div class="All-Container">

<div class="Reserve-Container">
    <div id="title" ><h2>Reserve Now</h2> </div> 
    <p id="reseve-text">HHHHHHHHHH HHHHHHH HHHH HHHHHHHHHHH HHHHHHHHHHH </p>
    <div class="my-form">
       <div> 
        <input  [(ngModel)]="customer.name" type="text" placeholder="Name"> <i class="fa-solid fa-pen" style="color: #ffffff;"></i>
    </div>
       <div> 
        <input [(ngModel)]="customer.email" type="email" placeholder="Email"> <i class="fa-regular fa-envelope" style="color: #ffffff;"></i>
    </div>
       <div>
         <input [(ngModel)]="customer.phone" type="text" placeholder="Phone"> <i class="fa-solid fa-phone" style="color: #ffffff;"></i>
        </div>
       <div> 
        <input [(ngModel)]="customer.address" type="text" placeholder="Address"> <i class="fa-solid fa-location-dot" style="color: #ffffff;"></i>
    </div>


    </div>
    <div style="color: red;">{{this.errorMessage}}</div>
    <div class="btn-container">

        <a class="btn" (click)="GoBack()">BACK</a>
        <a class="btn" (click)="confirm()">CONFIRM</a>
        </div> 

</div>

 <div class="Order">
   <div class="Order-title"> <h3> <span>Todays </span>specail</h3>
    <div>$ 12.00</div>
   </div>
    <div> CASRERSALAD</div>
    <P>Lorem ipsam Lorem ipsam Lorem ipsam Lorem ipsamLorem ipsamLorem ipsamLorem ipsamLorem ipsamLorem ipsam</P>
