import { SearchService } from '../search.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent  {
[x: string]: any;
  cities = ['Mansoura', 'Alex', 'Cairo',''];

