    this.searchRestaurants();

  }
  city:string=''

  selectedCity: string=''
  selectedName:string=''
  searchRestaurants() {
    this.service.getRestaurants().subscribe((data:any)=>{
      this.restaurants=data
    })
    console.log(this.restaurants)
  }



