import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
/**
 *
 */
constructor(private fb:FormBuilder) { 
}
ngOnInit():void{
  this.createform
}
loginform!:FormGroup
createform(){
  this.loginform=this.fb.group({
    email:['',[Validators.required,Validators.email]],
    password:['',[Validators.required,Validators.minLength(4),Validators.maxLength(20)]]
  })
}
login(){}
}
<form  class="row g-3 needs-validation" #form="ngForm" (ngSubmit)="Login(form)">
<div class="col-md-4">
    <label for="validationCustomUsername" class="form-label">Email</label>
    <div  class="input-group has-validation">
      <span class="input-group-text" id="inputGroupPrepend">@</span>
      <input ngModel name="email" #email="ngModel" type="text" class="form-control" id="validationCustomUsername" aria-describedby="inputGroupPrepend" required email>
      <div class="alert alert-danger" *ngIf="email.invalid && email.errors" >email is Required</div>
    </div>
  </div>

  <div class="col-md-4">
    <label for="validationCustom01" class="form-label">password</label>
    <input ngModel name="password" #password="ngModel"  type="password" class="form-control" id="validationCustom01" value="Mark" required>
    <div class="alert alert-danger" *ngIf="password.invalid&&password.touched">Password is Required</div>
  </div>

  <div class="col-12">
    <button type="submit" class="btn btn-primary" [disabled]="form.invalid">Register</button>
  </div>
</form>
